sys_script.script:
(function executeRule(current, previous /*null when async*/ ) {
    if (current.state != previous.state) {

        // to get learner group sys_id
        var group = current.assignment_group;

        // convert skill list to array
        var list = current.skills.toString();
        var array = list.split(',');

        // query the group members
        var learner = new GlideRecord('sys_user_grmember');
        learner.addQuery('group', group);
        learner.query();

        while (learner.next()) {

            // append skills to users
            appendSkills(learner.user, array);
        }
    }

})(current, previous);

function appendSkills(user_id, skill_array) {
    for (var i = 0; i < skill_array.length; i++) {
        var gr = new GlideRecord('sys_user_has_skill');
        gr.initialize();
        gr.user = user_id;
        gr.skill = skill_array[i];
        gr.insert();
        gs.info('Skill ' + skill_array[i] + ' has been added to user ' + user_id.name);
    }
}
