sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }

	var dt1 = g_form.getValue('u_employment_start');
	var dt2 = g_form.getValue('u_employment_end');

	var new_dt1 = dt1[5] + dt1[6] + '/' + dt1[8] + dt1[9] + '/' + dt1[0] + dt1[1] + dt1[2] + dt1[3];
	var new_dt2 = dt2[5] + dt2[6] + '/' + dt2[8] + dt2[9] + '/' + dt2[0] + dt2[1] + dt2[2] + dt2[3];

	var date1 = new Date(new_dt1);
    var date2 = new Date(new_dt2);

	var months = monthDiff(date1, date2);
	g_form.setValue('u_employment_dur', months);

    function monthDiff(d1, d2) {
        var months;
        months = (d2.getFullYear() - d1.getFullYear()) * 12;
        months -= d1.getMonth();
        months += d2.getMonth();
        return months <= 0 ? 0 : months;

    }
   //Type appropriate comment here, and begin script below

}
