sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    g_form.clearMessages();
    g_form.setValue('u_bat_weeks', 'Error');

	// get string value of both dates
    var dt1 = g_form.getValue('u_bat_start');
    var dt2 = g_form.getValue('u_bat_end');

	// reconstruct date string into JavaScript date format
    var new_dt1 = dt1[5] + dt1[6] + '/' + dt1[8] + dt1[9] + '/' + dt1[0] + dt1[1] + dt1[2] + dt1[3];
    var new_dt2 = dt2[5] + dt2[6] + '/' + dt2[8] + dt2[9] + '/' + dt2[0] + dt2[1] + dt2[2] + dt2[3];

	// create JavaScript date object using input dates
    var date1 = new Date(new_dt1);
    var date2 = new Date(new_dt2);

	// check if the from date is Monday
    if (date2.getDay() != 0) {
        g_form.addErrorMessage('The batch must end on the Sunday of the week.');
    } else {
        var diff_ms = date2.getTime() - date1.getTime();
        var diff_days = diff_ms / (1000 * 3600 * 24) + 1;
        var diff_weeks = diff_days / 7;

		// check for valid calculation
        if (diff_weeks > 0) {
            g_form.setValue('u_bat_weeks', diff_weeks);
        } else {
            g_form.addErrorMessage('Invalid date input.');
        }
    }

}
