sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }

    if (g_form.getValue('u_present')) {
        g_form.setVisible('u_employment_end', false);

        var today_date = new Date();
        var today = formatDate(today_date, g_user_date_format);
        g_form.setValue('u_employment_end', today);
    }
    if (g_form.getValue('u_present') == 'false') {
        g_form.setVisible('u_employment_end', true);
    }

    //Type appropriate comment here, and begin script below

}
