sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (isLoading || newValue === '') {
        return;
    }
    g_form.clearMessages();
    g_form.setValue('u_week_num', 'Error');

    var dt1 = g_form.getValue('u_mod_start');

    var new_dt1 = dt1[5] + dt1[6] + '/' + dt1[8] + dt1[9] + '/' + dt1[0] + dt1[1] + dt1[2] + dt1[3];

    var date1 = new Date(new_dt1);
    var today = new Date();

    // check if the from date is a future Monday
    if (date1.getDay() != 1 || date1 <= today) {
        g_form.addErrorMessage('The course must start on a future Monday.');
    } else {
        g_form.setValue('u_week_num', 'Week 0');
    }

}
